/**
 * @param {string} name 
 */

function logready(name) {
    !name.length ? name = "Your Bot" : name
    let amount = name.length;

    let line = '─'.repeat(amount);
    let space = ' '.repeat(amount)

    console.log(`╭${line}─────────────────────────────────────────────────────╮`)
    console.log(`│${space}                                                     │`)
    console.log(`│${space}                                                     │`)
    console.log(`│               ${name} is online and updated!               │`)
    console.log(`│${space}                                                     │`)
    console.log(`│${space}                                                     │`)
    console.log(`╰${line}─────────────────────────────────────────────────────╯`)
};

module.exports = logready;